sysauto_script.condition:
//Create the answer variable
 var answer = true;
 var rightnow = new GlideDateTime();
var dayofweek = rightnow.getDayOfWeekLocalTime();

if(dayofweek==6||dayofweek==7){
	answer = false;
}
answer;

sysauto_script.script:
 var overduetask=new GlideRecord('x_58872_needit_needit_task');
 overduetask.addQuery('due_date','<=', rightnow);
 overduetask.addQuery('state', '!=',3);
overduetask.query();
while(overduetask.hasNext()){
	gs.info("Overdue NeedIt Task record :", overduetask.number);
	gs.eventQueue('x_58872_needit.overduetask',overduetask, overduetask.number,gs.getUserName());
}
